window.addEventListener("load", (event) => {
	// If the record ID is valid but the record has not been saved at least once, trigger automatic "Save & Stay" so that "Send Email" button will function appropriately
	if(document.querySelector('input[name="ref_id_arm4_valid"]').value == '1' && document.querySelector('#nemail_loading-tr').getAttribute("style") == "display: table-row;") {
		;
		setTimeout(function(){
  		document.getElementById("submit-btn-savecontinue").click()
		}, 1000);
	}

	// Begin by temporarily disabling all checkboxes for sites to email while database checks to see which have no defined emails on the "Notification Email Addresses" form
	document.querySelectorAll('input[name="__chkn__nemail_which_sites"]').forEach(cb => {
	  cb.disabled = true;
	});

	// Temporarily disable "Send Email" button, if it is present
	if(document.querySelector('#nemail_sent_date-tr > td:nth-child(2) > div:nth-child(2) > button[name="nctu-alert-button"]')) {
		document.querySelector('#nemail_sent_date-tr > td:nth-child(2) > div:nth-child(2) > button[name="nctu-alert-button"]').disabled = true
		document.querySelector('#nemail_sent_date-tr > td:nth-child(2) > div:nth-child(2) > button[name="nctu-alert-button"]').classList.add("ui-state-disabled");
	}

	//Get the noaddresses calc field and monitor for programmatic changes to the value
	const nemail_sites_noaddresses = document.querySelector('input[name="nemail_sites_noaddresses"]');
	const desc_nemail_sites_noaddresses = Object.getOwnPropertyDescriptor(HTMLInputElement.prototype, "value");
	Object.defineProperty(nemail_sites_noaddresses, "value", {
	    get: desc_nemail_sites_noaddresses.get,
	    set: function(v) {
	        desc_nemail_sites_noaddresses.set.call(this, v);
	        const excluded = v.split(',').map(v => v.trim());
					document.querySelectorAll('input[name="__chkn__nemail_which_sites"]').forEach(cb => {
						if(excluded.includes(cb.getAttribute('code'))) {
							// Deselect checkboxes that are checked but should be disabled
							cb.checked = false;
						} else {
							// Enable checkboxes that have email addresses
							cb.disabled = false;
						}
					});
	    }
	});

	// Get the recipients, subject, and message fields
	const nemail_addresses = document.querySelector('input[name="nemail_addresses"]');
	const nemail_subject = document.querySelector('input[name="nemail_addresses"]');
	const nemail_message = document.querySelector('textarea[name="nemail_message"]');

	// Monitor the recipients field for programmatic changes to the value and check if the email can be sent when it changes
	const desc_nemail_addresses = Object.getOwnPropertyDescriptor(HTMLInputElement.prototype, "value");
	Object.defineProperty(nemail_addresses, "value", {
	    get: desc_nemail_addresses.get,
	    set: function(v) {
	        desc_nemail_addresses.set.call(this, v);
	        checkIfEmailCanBeSent(nemail_addresses.value,nemail_subject.value,nemail_message.value)
	    }
	});

	// Monitor the subject and message fields for changes to the values and check if the email can be sent when they change
	nemail_subject.addEventListener("change", checkIfEmailCanBeSent(nemail_addresses.value,nemail_subject.value,nemail_message.value));
	nemail_message.addEventListener("change", checkIfEmailCanBeSent(nemail_addresses.value,nemail_subject.value,nemail_message.value));
});

function checkIfEmailCanBeSent(recipients,subject,message) {
	// Checks if at least one recipient, subject, and message are defined and enables the "Send email" button if so.
	if(document.querySelector('#nemail_sent_date-tr > td:nth-child(2) > div:nth-child(2) > button[name="nctu-alert-button"]') && trim(recipients) != '' && trim(subject) != '' && trim(message) != '') {
		// Enable "Send Email" button
		document.querySelector('#nemail_sent_date-tr > td:nth-child(2) > div:nth-child(2) > button[name="nctu-alert-button"]').disabled = false
		document.querySelector('#nemail_sent_date-tr > td:nth-child(2) > div:nth-child(2) > button[name="nctu-alert-button"]').classList.remove("ui-state-disabled");
	}


    // Add tinymce editor to allow better editing of message contents
    tinymce.init({
        license_key: 'gpl',
        font_family_formats: 'Open Sans=Open Sans; Andale Mono=andale mono,times; Arial=arial,helvetica,sans-serif; Arial Black=arial black,avant garde; Book Antiqua=book antiqua,palatino; Comic Sans MS=comic sans ms,sans-serif; Courier New=courier new,courier; Georgia=georgia,palatino; Helvetica=helvetica; Impact=impact,chicago; Symbol=symbol; Tahoma=tahoma,arial,helvetica,sans-serif; Terminal=terminal,monaco; Times New Roman=times new roman,times; Trebuchet MS=trebuchet ms,geneva; Verdana=verdana,geneva; Webdings=webdings; Wingdings=wingdings,zapf dingbats',
        promotion: false,
        entity_encoding : "raw",
        default_link_target: '_blank',
        selector: 'textarea#nemail_message',
        menubar: false,
        branding: false,
        statusbar: true,
        elementpath: false, // Hide this, since it oddly renders below the textarea.
        plugins: 'autolink lists link searchreplace code table directionality hr',
        toolbar1: 'fontfamily blocks fontsize bold italic underline strikethrough forecolor backcolor',
        toolbar2: 'align bullist numlist outdent indent table pre hr link fullscreen searchreplace removeformat undo redo code',
        contextmenu: "copy paste | link image inserttable | cell row column deletetable",
        content_css: `${window.location.href.substring(0, window.location.href.indexOf("DataEntry"))}Resources/webpack/css/bootstrap.min.css,${window.location.href.substring(0, window.location.href.indexOf("DataEntry"))}Resources/webpack/css/fontawesome/css/all.min.css,${window.location.href.substring(0, window.location.href.indexOf("DataEntry"))}Resources/css/style.css`,
        relative_urls: false,
